import React from "react";

const HeroSection = () => {
  return (
    <div style={styles.heroContainer}>
      <p style={styles.topText}>Learn Robotics. Build the Future.</p>

      <h1 style={styles.mainHeading}>
        <span style={styles.greenText}>Think Limitless</span>{" "}
        <span style={styles.blackText}>Learn</span>
        <br />
        <span style={styles.blackText}>Beyond Boundaries</span>
      </h1>

      <p style={styles.description}>
        At Bidyut, we go beyond textbooks â€” offering practical robotics learning
        powered by AI, coding, and STEAM. Discover a new way to build, program,
        and solve real-world challenges with creativity.
      </p>
    </div>
  );
};

const styles = {
  heroContainer: {
    padding: "4rem 2rem",
    textAlign: "left",
    maxWidth: "900px",
    margin: "0 auto",
  },
  topText: {
    fontSize: "18px",
    fontWeight: 600,
    color: "#00C68A",
    marginBottom: "20px",
    backgroundColor: "#E8F9F2",
    display: "inline-block",
    padding: "6px 14px",
    borderRadius: "8px",
  },
  mainHeading: {
    fontSize: "48px",
    fontWeight: 800,
    lineHeight: 1.2,
    marginBottom: "24px",
  },
  greenText: {
    color: "#00C68A",
  },
  blackText: {
    color: "#000",
  },
  description: {
    fontSize: "16px",
    color: "#5E5E5E",
    maxWidth: "600px",
    lineHeight: 1.7,
    fontWeight: 400,
  },
};

export default HeroSection;
