import React from "react";

interface Offering {
    title: string;
    description: string;
    image: string; // Use a valid image URL or import.
    highlight?: boolean;
}

const offerings: Offering[] = [
    {
        title: "Smart Progress Tracking",
        description:
            "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
        image: "https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=400&h=250&q=80",
    },
    {
        title: "Smart Progress Tracking",
        description:
            "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
        image: "https://images.unsplash.com/photo-1521737852567-6949f3f9f2b5?auto=format&fit=crop&w=400&h=250&q=80",
    },
    {
        title: "Collaborative Learning Spaces",
        description:
            "Connect with peers worldwide in virtual study rooms where you can share resources, discuss concepts, and solve problems together.",
        image: "https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=400&h=250&q=80",
        highlight: true,
    },
    {
        title: "Smart Progress Tracking",
        description:
            "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
        image: "https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=400&h=250&q=80",
    },
    {
        title: "Smart Progress Tracking",
        description:
            "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
        image: "https://images.unsplash.com/photo-1521737852567-6949f3f9f2b5?auto=format&fit=crop&w=400&h=250&q=80",
    },
];

const Offerings: React.FC = () => {
    return (
        <div className="bg-gray-50 py-12">
            <div className="text-center mb-8">
                <h2 className="text-4xl font-bold">
                    Our <span className="text-green-500">Offerings</span>
                </h2>
                <p className="text-gray-500 mt-3 max-w-xl mx-auto">
                    Discover how Bidyut is transforming education and industry through innovative robotics solutions
                </p>
            </div>
            <div className="flex items-center justify-center overflow-x-auto space-x-4 px-4 pb-10">
                {offerings.map((offering, idx) => (
                    <div
                        key={idx}
                         ref={(el) => (cardRefs.current[idx] = el)}
                        className="flex-none bg-white shadow-lg rounded-xl overflow-hidden transition-all duration-300 ease-out ring-4 ring-emerald-100"  >
                        <div className="p-0">
                            <Image
                                src={offering.image || "/placeholder.svg"}
                                alt={offering.title}
                                className="w-full h-36 object-cover"
                            />
                            <div className="p-4">
                                <h3 className="text-lg font-bold mb-1">{offering.title}</h3>
                                <p className="text-gray-600 text-xs">{offering.description}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Offerings;
