import React from "react";

const HeroSection = () => {
  return (
    <section className="relative w-full h-screen overflow-hidden bg-gradient-to-br from-blue-50 to-gray-100">
      {/* Background Image/Video */}
      <div className="absolute top-0 right-0 w-1/2 h-full">
        <img
          className="w-full h-full object-cover"
          src="https://images.unsplash.com/photo-1485827404703-89b55fcc595e?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          alt="Robot"
        />
        <div className="absolute inset-0 bg-white/40"></div>
      </div>

      {/* Content Container */}
      <div className="relative h-full flex items-center">
        <div className="w-full max-w-7xl mx-auto px-8 lg:px-16">
          <div className="w-1/2 pr-8">
            {/* Badge */}
            <div className="inline-block mb-6">
              <span className="bg-white text-green-500 font-semibold text-sm border-2 border-green-500 px-4 py-2 rounded-lg shadow-sm">
                Learn Robotics. Build the Future.
              </span>
            </div>

            {/* Main Heading */}
            <h1 className="text-5xl lg:text-6xl xl:text-7xl font-bold leading-tight mb-8">
              <span className="text-green-500">Think Limitless</span>{" "}
              <span className="text-black">Learn</span>
              <br />
              <span className="text-black">Beyond Boundaries</span>
            </h1>

            {/* Paragraph */}
            <p className="text-gray-600 text-lg leading-relaxed max-w-2xl">
              At Bidyut, we go beyond textbooks â€” offering practical robotics
              learning powered by AI, coding, and STEAM. Discover a new way to
              build, program, and solve real-world challenges with creativity.
            </p>
          </div>
        </div>
      </div>

      {/* Navigation Dots */}
      <div className="absolute bottom-12 left-1/2 transform -translate-x-1/2 flex space-x-3">
        <div className="w-3 h-3 bg-green-500 rounded-full"></div>
        <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
        <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
      </div>
    </section>
  );
};

export default HeroSection;
