import React from "react";

const HeroSection = () => {
  return (
    <section className="relative w-full h-screen font-sans overflow-hidden">
      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover -z-20"
        src="/Robo Dance.mp4"
        autoPlay
        loop
        muted
        playsInline
      />

      {/* Overlay */}
      <div className="absolute top-0 left-0 w-full h-full bg-white opacity-60 -z-10" />

      {/* Content Container */}
      <div className="relative h-full flex items-center">
        <div className="max-w-4xl mx-auto px-10 lg:px-16">
          {/* Badge */}
          <div className="inline-block mb-5">
            <span className="bg-white text-green-400 font-semibold text-sm border-2 border-green-400 px-4 py-2 rounded-md">
              Learn Robotics. Build the Future.
            </span>
          </div>

          {/* Main Heading */}
          <h1 className="text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-semibold leading-tight mb-6 max-w-3xl">
            <span className="text-green-400">Think Limitless</span>{" "}
            <span className="text-black">Learn</span>
            <br />
            <span className="text-black">Beyond Boundaries</span>
          </h1>

          {/* Paragraph */}
          <p className="text-gray-500 text-base lg:text-lg max-w-2xl leading-relaxed">
            At Bidyut, we go beyond textbooks â€” offering practical robotics
            learning powered by AI, coding, and STEAM. Discover a new way to
            build, program, and solve real-world challenges with creativity.
          </p>
        </div>
      </div>

      {/* Navigation Dots */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-3">
        <div className="w-3 h-3 bg-green-400 rounded-full"></div>
        <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
        <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
      </div>
    </section>
  );
};

export default HeroSection;
