"use client"

import { useRef, useEffect, useState, useCallback } from "react"

interface Offering {
  image: string
  title: string
  description: string
}

const offerings: Offering[] = [
  {
    image: "/placeholder.svg?height=150&width=250",
    title: "Smart Progress Tracking",
    description:
      "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement. Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
  },
  {
    image: "/placeholder.svg?height=150&width=250",
    title: "Collaborative Learning Spaces",
    description:
      "Connect with peers worldwide in virtual study rooms where you can share resources, discuss concepts, and solve problems together.",
  },
  {
    image: "/placeholder.svg?height=150&width=250",
    title: "Smart Progress Tracking",
    description:
      "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement. Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
  },
  {
    image: "/placeholder.svg?height=150&width=250",
    title: "Personalized Learning Paths",
    description:
      "Tailor your educational experience with adaptive learning paths that adjust to your pace and preferences, ensuring optimal engagement and retention.",
  },
  {
    image: "/placeholder.svg?height=150&width=250",
    title: "Interactive Simulations",
    description:
      "Engage with realistic simulations and virtual labs to apply theoretical knowledge in practical scenarios, enhancing understanding and skill development.",
  },
]

export default function OfferingsCarousel() {
  const containerRef = useRef<HTMLDivElement>(null)
  const [centerIdx, setCenterIdx] = useState(2)
  const [hasUserInteracted, setHasUserInteracted] = useState(false)
  const cardCount = offerings.length

  // For arrow nav and auto-advance
  useEffect(() => {
    if (hasUserInteracted) return
    const interval = setInterval(() => {
      setCenterIdx(idx => (idx + 1) % cardCount)
    }, 3400)
    return () => clearInterval(interval)
  }, [cardCount, hasUserInteracted])

  // Scroll to center card whenever it changes
  useEffect(() => {
    if (!containerRef.current) return
    const children = Array.from(containerRef.current.children)
    const centerCard = children[centerIdx] as HTMLElement | undefined
    if (centerCard) {
      containerRef.current.scrollTo({
        left:
          centerCard.offsetLeft -
          containerRef.current.offsetWidth / 2 +
          centerCard.offsetWidth / 2,
        behavior: "smooth",
      })
    }
  }, [centerIdx])

  // Card transformation logic: curve, scale, opacity
  const getCardStyle = (i: number) => {
    const offset = i - centerIdx
    const absOffset = Math.abs(offset)

    // For max offset of 2 (two cards on each side), else hide (opacity 0, pointerEvents none)
    if (absOffset > 2)
      return {
        opacity: 0,
        pointerEvents: "none",
        transform: "scale(0.85) translateY(40px)",
        zIndex: 1,
      }

    // These values were tuned to visually match the reference image.
    const baseScale = 0.90
    const scale = 1 - 0.12 * absOffset
    const y = absOffset === 2 ? 50 : absOffset === 1 ? 22 : 0
    const rotate = offset * (absOffset === 2 ? 22 : 13) // More rotate for far cards

    // Slightly less opaque and lighter background as they get farther
    const opacity = absOffset === 0 ? 1 : absOffset === 1 ? 0.76 : 0.44

    // Z-index: center is top, then side1, then side2
    const z =
      4 -
      absOffset // so center(4), side(3), far-side(2), for overlap

    // Background ring highlights: only on center
    return {
      opacity,
      transform: `perspective(900px) scale(${scale}) translateY(${y}px) rotateY(${rotate}deg)`,
      zIndex: z,
      background:
        absOffset === 0
          ? "white"
          : absOffset === 1
          ? "rgba(255,255,255,0.83)"
          : "rgba(255,255,255,0.62)",
      boxShadow:
        absOffset === 0
          ? "0px 10px 32px 0px rgb(12 64 53 / 9%)"
          : "0px 3px 15px 0px rgb(14 78 63 / 7%)",
      border:
        absOffset === 0 ? "2px solid #a7f3d0" : "1px solid #e5e7eb", // Emerald on center
      pointerEvents: absOffset === 0 ? "auto" : "none", // Only center card clickable/focusable
      transition:
        "transform 0.46s cubic-bezier(0.28,1.25,0.73,1), opacity 0.24s, box-shadow 0.28s, background 0.28s, border 0.28s",
    }
  }

  return (
    <section className="relative py-12 md:py-24 lg:py-32 overflow-x-clip">
      <div className="container px-4 md:px-6 text-center z-10 relative">
        <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl">
          Our <span className="text-emerald-500">Offerings</span>
        </h2>
        <p className="mx-auto max-w-[700px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed mt-4">
          Discover how Bidyut is transforming education and industry through innovative robotics solutions
        </p>
      </div>

      {/* Curved background */}
      <div className="absolute inset-x-0 top-1/2 -translate-y-1/2 h-[410px] bg-emerald-50 rounded-[150px] scale-x-110 opacity-80 z-0 pointer-events-none" />

      {/* Carousel + arrow controls */}
      <div className="relative mt-10 mb-3 flex justify-center items-center max-w-screen-xl mx-auto">
        <button
          aria-label="Previous"
          className="absolute left-2 z-10 bg-white/80 border border-emerald-100 shadow p-1 px-2.5 rounded-full hover:bg-emerald-100 transition hidden md:inline"
          onClick={() => {
            setHasUserInteracted(true)
            setCenterIdx(idx => (idx - 1 + cardCount) % cardCount)
          }}
          tabIndex={-1}
        >
          <svg width="21" height="21" fill="none" className="text-emerald-600"><path d="M13 16l-5-5 5-5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
        </button>

        {/* Card row; we make it wide enough to always allow centering */}
        <div
          ref={containerRef}
          className="flex flex-nowrap items-stretch justify-center gap-0 overflow-x-auto no-scrollbar w-full max-w-5xl px-0 select-none"
          style={{
            scrollBehavior: "smooth",
            perspective: "900px",
            alignItems: "center",
            paddingBottom: 24,
            paddingTop: 24,
            minHeight: 375,
          }}
          onMouseDown={() => setHasUserInteracted(true)}
          onTouchStart={() => setHasUserInteracted(true)}
        >
          {offerings.map((offering, i) => (
            <div
              key={offering.title + i}
              className="flex-none rounded-[1.5rem] mx-[-36px] transition-all"
              style={{
                width: i === centerIdx ? 350 : 235,
                minHeight: i === centerIdx ? 340 : 220,
                ...getCardStyle(i),
              }}
              tabIndex={i === centerIdx ? 0 : -1}
              aria-current={i === centerIdx ? "true" : "false"}
              aria-hidden={i !== centerIdx}
            >
              <div className="overflow-hidden rounded-[1.5rem] flex flex-col h-full">
                <div className={`relative w-full ${i === centerIdx ? "h-40" : "h-28"}`}>
                  <img
                    src={offering.image}
                    alt={offering.title}
                    className="object-cover w-full h-full"
                  />
                </div>
                <div className="px-8 py-6 flex-1 flex flex-col justify-between">
                  <h3
                    className={`font-bold text-lg mb-2 transition-colors ${
                      i === centerIdx ? "text-black" : "text-emerald-900/70"
                    }`}
                  >
                    {offering.title}
                  </h3>
                  <p
                    className={`text-gray-600 text-[13px] transition-colors ${
                      i === centerIdx
                        ? "text-gray-600"
                        : "text-emerald-900/45"
                    }`}
                  >
                    {offering.description}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <button
          aria-label="Next"
          className="absolute right-2 z-10 bg-white/80 border border-emerald-100 shadow p-1 px-2.5 rounded-full hover:bg-emerald-100 transition hidden md:inline"
          onClick={() => {
            setHasUserInteracted(true)
            setCenterIdx(idx => (idx + 1) % cardCount)
          }}
          tabIndex={-1}
        >
          <svg width="21" height="21" fill="none" className="text-emerald-600"><path d="M8 16l5-5-5-5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
        </button>
      </div>
    </section>
  )
}

// Extra: hide scrollbar utility (if not already present)
const style =
  `<style>
  .no-scrollbar::-webkit-scrollbar { display: none }
  .no-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
  </style>`
if (typeof window !== "undefined") {
  document.head.insertAdjacentHTML("beforeend", style)
}
