"use client"

import { useState } from "react"

interface Offering {
  title: string
  description: string
}

export default function OfferingsSection() {
  const [activeTab, setActiveTab] = useState(0)

  const offerings: Offering[] = [
    {
      title: "Collaborative Learning Spaces",
      description: "Connect with peers worldwide in virtual study rooms where you can share resources, discuss concepts, and solve problems together."
    },
    {
      title: "Smart Progress Tracking",
      description: "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement."
    },
    {
      title: "Interactive Workshops",
      description: "Engage in hands-on workshops led by experts to deepen your understanding and apply theoretical knowledge to real-world scenarios."
    },
    {
      title: "Robotics Simulation Labs",
      description: "Practice and experiment with robotics in a safe, virtual environment before deploying your solutions in the physical world."
    },
    {
      title: "Personalized Mentorship",
      description: "Receive one-on-one guidance from industry professionals to accelerate your growth and achieve your career goals."
    }
  ]

  return (
    <section className="py-16 px-4 sm:px-6 lg:px-8 max-w-4xl mx-auto">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">Our Offerings</h1>
        <p className="text-xl text-gray-600">
          Discover how Bidyut is transforming education and industry through innovative robotics solutions
        </p>
      </div>

      <div className="flex flex-col md:flex-row gap-8">
        {/* Navigation Tabs */}
        <div className="md:w-1/3">
          <div className="flex md:flex-col overflow-x-auto md:overflow-visible pb-2 md:pb-0 gap-2">
            {offerings.map((offering, index) => (
              <button
                key={index}
                onClick={() => setActiveTab(index)}
                className={`px-4 py-3 rounded-lg text-left whitespace-nowrap transition-colors ${activeTab === index 
                  ? 'bg-[#2ecc71] text-white font-medium' 
                  : 'bg-gray-100 hover:bg-gray-200 text-gray-800'}`}
              >
                {offering.title}
              </button>
            ))}
          </div>
        </div>

        {/* Content Area */}
        <div className="md:w-2/3">
          <div className="bg-white p-6 rounded-lg shadow-md h-full">
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">
              {offerings[activeTab].title}
            </h2>
            <p className="text-gray-600 leading-relaxed">
              {offerings[activeTab].description}
            </p>
          </div>
        </div>
      </div>
    </section>
  )
}
