import React from "react";

const HeroSection: React.FC = () => {
  return (
    <section style={{ position: "relative", width: "100%", height: "100vh", fontFamily: "'Poppins', sans-serif", overflow: "hidden" }}>
      {/* Google Font */}
      <link
        href="https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap"
        rel="stylesheet"
      />

      {/* Background Video */}
      <video
        style={{
          width: "100%",
          height: "100%",
          objectFit: "cover",
          position: "absolute",
          top: 0,
          left: 0,
          zIndex: -2,
        }}
        src="/public/Robo Dance.mp4" // ⬅️ Replace with your actual video path (inside /public)
        autoPlay
        loop
        muted
        playsInline
      />

      {/* Overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "#ffffff",
          opacity: 0.6,
          zIndex: -1,
        }}
      />

      {/* Content */}
      <div
        style={{
          position: "relative",
          maxWidth: "900px",
          padding: "80px 40px",
          margin: "0 auto",
          color: "#000000",
          textAlign: "left",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <div
          style={{
            background: "white",
            color: "#0acf83",
            fontWeight: 600,
            fontSize: "16px",
            border: "2px solid #0acf83",
            display: "inline-block",
            padding: "6px 14px",
            borderRadius: "6px",
            marginBottom: "20px",
          }}
        >
          Learn Robotics. Build the Future.
        </div>

        <h1
          style={{
            fontSize: "64px",
            fontWeight: 600,
            lineHeight: 1.2,
            margin: "0 0 20px 0",
          }}
        >
          <span style={{ color: "#0acf83" }}>Think Limitless</span> Learn <br />
          <span style={{ color: "#000000" }}>Beyond Boundaries</span>
        </h1>

        <p
          style={{
            fontSize: "16px",
            color: "rgba(0, 0, 0, 0.44)",
            maxWidth: "640px",
          }}
        >
          At Bidyut, we go beyond textbooks — offering practical robotics
          learning powered by AI, coding, and STEAM. Discover a new way to
          build, program, and solve real-world challenges with creativity.
        </p>
      </div>
    </section>
  );
};

export default HeroSection;
