"use client"

import { useState, useEffect } from "react"

interface Offering {
  image: string
  title: string
  description: string
}

const offerings: Offering[] = [
  {
    image: "/placeholder.svg?height=200&width=300&text=Learning%20Spaces",
    title: "Collaborative Learning Spaces",
    description: "Connect with peers worldwide in virtual study rooms where you can share resources, discuss concepts, and solve problems together.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Progress%20Tracking",
    title: "Smart Progress Tracking",
    description: "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Workshops",
    title: "Interactive Workshops",
    description: "Engage in hands-on workshops led by experts to deepen your understanding and apply theoretical knowledge to real-world scenarios.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Simulation%20Labs",
    title: "Robotics Simulation Labs",
    description: "Practice and experiment with robotics in a safe, virtual environment before deploying your solutions in the physical world.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Mentorship",
    title: "Personalized Mentorship",
    description: "Receive one-on-one guidance from industry professionals to accelerate your growth and achieve your career goals.",
  },
]

interface CardStyle {
  transform: string
  zIndex: number
  opacity: number
  width: string
  pointerEvents: 'auto' | 'none'
}

const getCardStyles = (position: number, total: number): CardStyle => {
  const centerIndex = Math.floor(total / 2)
  const distance = Math.abs(position - centerIndex)
  
  if (position === centerIndex) {
    // Center card
    return {
      transform: 'translateX(-50%) scale(1) rotateY(0deg)',
      zIndex: 20,
      opacity: 1,
      width: '500px',
      pointerEvents: 'auto'
    }
  } else if (position < centerIndex) {
    // Left cards
    const offset = (centerIndex - position) * 120
    const scale = 1 - (distance * 0.15)
    return {
      transform: `translateX(calc(-50% - ${offset}px)) scale(${scale}) rotateY(20deg)`,
      zIndex: 20 - distance,
      opacity: scale,
      width: `${400 - (distance * 50)}px`,
      pointerEvents: 'none'
    }
  } else {
    // Right cards
    const offset = (position - centerIndex) * 120
    const scale = 1 - (distance * 0.15)
    return {
      transform: `translateX(calc(-50% + ${offset}px)) scale(${scale}) rotateY(-20deg)`,
      zIndex: 20 - distance,
      opacity: scale,
      width: `${400 - (distance * 50)}px`,
      pointerEvents: 'none'
    }
  }
}

export default function OfferingsCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0)
  const len = offerings.length

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % len)
    }, 3000)

    return () => clearInterval(interval)
  }, [len])

  return (
    <section className="relative w-full py-12 md:py-24 lg:py-32 overflow-hidden">
      <div className="container px-4 md:px-6 text-center relative z-10">
        <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl mb-4">
          Our <span className="text-[#2ecc71]">Offerings</span>
        </h2>
        <p className="mx-auto max-w-[700px] text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
          Discover how Bidyut is transforming education and industry through innovative robotics solutions
        </p>

        {/* Carousel container */}
        <div className="relative h-[500px] md:h-[600px] w-full mt-12">
          <div className="absolute inset-0 flex items-center justify-center perspective-1000">
            <div className="relative w-full h-full">
              {offerings.map((offering, index) => {
                // Calculate the visual position in the carousel
                const visualPosition = (index - currentIndex + len) % len
                const styles = getCardStyles(visualPosition, len)

                return (
                  <div
                    key={index}
                    className="absolute top-1/2 left-1/2 transition-all duration-500 ease-out"
                    style={{
                      transform: styles.transform,
                      zIndex: styles.zIndex,
                      opacity: styles.opacity,
                      width: styles.width,
                      pointerEvents: styles.pointerEvents,
                      backfaceVisibility: 'hidden',
                      transformStyle: 'preserve-3d'
                    }}
                  >
                    <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col items-center text-center h-full mx-auto">
                      <div className="relative w-full h-48 mb-4 rounded-md overflow-hidden">
                        <img
                          src={offering.image}
                          alt={offering.title}
                          className="rounded-md w-full h-full object-cover"
                          width={300}
                          height={200}
                        />
                      </div>
                      <h3 className="text-xl font-semibold mb-2">{offering.title}</h3>
                      <p className="text-gray-600 text-sm">{offering.description}</p>
                    </div>
                  </div>
                )
              })}
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
