import { useState, useEffect } from "react"
// import Image from "next/image"

interface Offering {
  image: string
  title: string
  description: string
}

// Define the offerings data with distinct placeholder images
const offerings: Offering[] = [
  {
    image: "/placeholder.svg?height=200&width=300&text=Collaborative",
    title: "Collaborative Learning Spaces",
    description:
      "Connect with peers worldwide in virtual study rooms where you can share resources, discuss concepts, and solve problems together.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Progress",
    title: "Smart Progress Tracking",
    description:
      "Monitor your learning journey with detailed analytics and insights that help you identify strengths and areas for improvement.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Workshops",
    title: "Interactive Workshops",
    description:
      "Engage in hands-on workshops led by experts to deepen your understanding and apply theoretical knowledge to real-world scenarios.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Robotics",
    title: "Robotics Simulation Labs",
    description:
      "Practice and experiment with robotics in a safe, virtual environment before deploying your solutions in the physical world.",
  },
  {
    image: "/placeholder.svg?height=200&width=300&text=Mentorship",
    title: "Personalized Mentorship",
    description:
      "Receive one-on-one guidance from industry professionals to accelerate your growth and achieve your career goals.",
  },
]

// Define styles for each card position (relative to the center card)
interface CardStyle {
  left: string
  transform: string
  zIndex: number
  filter: string
  opacity: number
  widthClass: string
  transformOrigin: string
}

const getCardStyles = (position: number): CardStyle => {
  switch (position) {
    case 0: // Center card
      return {
        left: "50%",
        transform: "translateX(-50%) scale(1) rotateY(0deg)",
        zIndex: 20,
        filter: "blur(0px)", // No blur
        opacity: 1,
        widthClass: "w-[300px] md:w-[400px] lg:w-[500px]",
        transformOrigin: "center center",
      }
    case -1: // Inner left: bend towards center, reduced separation
      return {
        left: "50%", // Start from center
        transform: "translateX(calc(-50% - 25vw)) scale(0.9) rotateY(-20deg)", // Shift left, scale, rotate towards center
        zIndex: 15,
        filter: "blur(0px)", // No blur
        opacity: 1, // Fully visible
        widthClass: "w-[250px] md:w-[350px] lg:w-[450px]",
        transformOrigin: "right center", // Rotate around right edge
      }
    case 1: // Inner right: bend towards center, reduced separation
      return {
        left: "50%", // Start from center
        transform: "translateX(calc(-50% + 25vw)) scale(0.9) rotateY(20deg)", // Shift right, scale, rotate towards center
        zIndex: 15,
        filter: "blur(0px)", // No blur
        opacity: 1, // Fully visible
        widthClass: "w-[250px] md:w-[350px] lg:w-[450px]",
        transformOrigin: "left center", // Rotate around left edge
      }
    case -2: // Outer left: bend towards center, reduced separation
      return {
        left: "50%", // Start from center
        transform: "translateX(calc(-50% - 50vw)) scale(0.8) rotateY(-40deg)", // Shift further left, scale, rotate towards center
        zIndex: 10,
        filter: "blur(0px)", // No blur
        opacity: 1, // Fully visible
        widthClass: "w-[200px] md:w-[300px] lg:w-[400px]",
        transformOrigin: "right center", // Rotate around right edge
      }
    case 2: // Outer right: bend towards center, reduced separation
      return {
        left: "50%", // Start from center
        transform: "translateX(calc(-50% + 50vw)) scale(0.8) rotateY(40deg)", // Shift further right, scale, rotate towards center
        zIndex: 10,
        filter: "blur(0px)", // No blur
        opacity: 1, // Fully visible
        widthClass: "w-[200px] md:w-[300px] lg:w-[400px]",
        transformOrigin: "left center", // Rotate around left edge
      }
    default:
      // For cards that are not currently visible (e.g., if offerings.length > 5)
      return {
        left: "50%",
        transform: "translateX(-50%) scale(0) rotateY(0deg)",
        zIndex: 5,
        filter: "blur(0px)", // No blur
        opacity: 0,
        widthClass: "w-0",
        transformOrigin: "center center",
      }
  }
}

export default function OfferingsCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0)
  const len = offerings.length

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % len)
    }, 3000) // Change every 3 seconds
    return () => clearInterval(interval)
  }, [len])

  // Define the relative positions for the 5 displayed cards
  const displayedCardRelativePositions = [-2, -1, 0, 1, 2]

  return (
    <section className="relative w-full py-12 md:py-24 lg:py-32 overflow-hidden flex flex-col justify-center items-center">
      <div className="container text-center relative z-10 w-full ">
        <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl mb-4">
          Our <span className="text-[#2ecc71]">Offerings</span>
        </h2>
        <p className="mx-auto text-gray-500 md:text-xl/relaxed lg:text-base/relaxed xl:text-xl/relaxed dark:text-gray-400">
          Discover how Bidyut is transforming education and industry through innovative robotics solutions
        </p>
        {/* Carousel container with its own background and rounded corners */}
        <div className="relative flex justify-center items-center h-[500px] bg-[#e0f7fa] w-full ">
          {displayedCardRelativePositions.map((relativePos) => {
            // Calculate the actual index in the offerings array, handling wrap-around
            const displayIndex = (currentIndex + relativePos + len) % len
            const offering = offerings[displayIndex]
            const styles = getCardStyles(relativePos)
            return (
              <div
                key={displayIndex} // Use the actual index from the offerings array as key
                className={`absolute top-10 transition-all duration-700 ease-in-out backface-hidden ${styles.widthClass}`}
                style={{
                  left: styles.left,
                  transform: styles.transform,
                  zIndex: styles.zIndex,
                  filter: styles.filter,
                  opacity: styles.opacity,
                  transformOrigin: styles.transformOrigin,
                }}
              >
                <div className="bg-white rounded-lg shadow-lg py-8 px-6 flex flex-col items-center text-center h-full max-w-96 gap-6">
                  <div className="relative w-full h-48 mb-4 rounded-md overflow-hidden">
                    <img
                      src={offering.image || "/placeholder.svg"}
                      alt={offering.title}
                      width={300}
                      height={200}
                      className="rounded-md absolute inset-0 w-full h-full object-cover"
                    />
                  </div>
                  <h3 className="text-xl font-semibold mb-2">{offering.title}</h3>
                  <p className="text-gray-600 text-sm">{offering.description}</p>
                </div>
              </div>
            )
          })}
        </div>
      </div>
    </section>
  )
}
